#include "zmbt/model/keyword_classifier.hpp"


namespace zmbt {
namespace expr {
namespace detail {


Classifier getKeywordClassifier(Keyword const& k)
{
    switch (k)
    {
    case Keyword::Literal:
    case Keyword::Void:
        return Classifier::Internal;
    case Keyword::Noop:
    case Keyword::Null:
    case Keyword::True:
    case Keyword::False:
    case Keyword::Pi:
    case Keyword::E:
    case Keyword::Inf:
    case Keyword::Eps:
    case Keyword::NaN:
        return Classifier::Const;
    case Keyword::Bool:
    case Keyword::Nil:
    case Keyword::Not:
    case Keyword::Neg:
    case Keyword::BitNot:
    case Keyword::Approx:
        return Classifier::UnaryOp;
    case Keyword::Sqrt:
    case Keyword::Sin:
    case Keyword::Cos:
    case Keyword::Tan:
    case Keyword::Asin:
    case Keyword::Acos:
    case Keyword::Atan:
    case Keyword::Sinh:
    case Keyword::Cosh:
    case Keyword::Tanh:
    case Keyword::Asinh:
    case Keyword::Acosh:
    case Keyword::Atanh:
    case Keyword::Exp:
    case Keyword::Erf:
    case Keyword::Erfc:
    case Keyword::Gamma:
    case Keyword::Abs:
    case Keyword::Ceil:
    case Keyword::Floor:
    case Keyword::Sign:
    case Keyword::Id:
        return Classifier::UnaryMathFn;
    case Keyword::Add:
    case Keyword::Sub:
    case Keyword::SubFrom:
    case Keyword::Mul:
    case Keyword::Div:
    case Keyword::DivFrom:
    case Keyword::Pow:
    case Keyword::PowFrom:
    case Keyword::Log:
    case Keyword::LogFrom:
    case Keyword::Mod:
    case Keyword::ModFrom:
    case Keyword::Quot:
    case Keyword::QuotFrom:
    case Keyword::BitAnd:
    case Keyword::BitOr:
    case Keyword::BitXor:
    case Keyword::BitLshift:
    case Keyword::BitLshiftFrom:
    case Keyword::BitRshift:
    case Keyword::BitRshiftFrom:
    case Keyword::Eq:
    case Keyword::Ne:
    case Keyword::Lt:
    case Keyword::Le:
    case Keyword::Gt:
    case Keyword::Ge:
    case Keyword::And:
    case Keyword::Or:
    case Keyword::SetEq:
    case Keyword::Subset:
    case Keyword::Superset:
    case Keyword::ProperSubset:
    case Keyword::ProperSuperset:
    case Keyword::In:
    case Keyword::NotIn:
    case Keyword::Ni:
    case Keyword::NotNi:
    case Keyword::Union:
    case Keyword::Intersect:
    case Keyword::Diff:
    case Keyword::DiffFrom:
        return Classifier::BinaryOp;
    default:
        return Classifier::Special;
    }

}

} // namespace detail
} // namespace expr
} // namespace zmbt
